mapper.select:
  select ,FWMC,JZLBMC from (
		select k.*, rownum rnum from
		(select u.*,r.FWMC,h.JZLBMC　 from fwzyffzcb_his u left join FWZYZCB r on u.FWBS=r.FWBS left join JZLBDMB h on u.JZFL=h.JZLBDM     where 1=1
  		 and u.FWBS = #{fwbs,jdbcType=VARCHAR}
		 and u.FFBS = #{ffbs,jdbcType=VARCHAR}

		 and u.FFMC like '%${ffmc}%'
		 and u.JZFL = #{jzfl,jdbcType=VARCHAR}
		 and u.SFHCSJ = #{sfhcsj,jdbcType=VARCHAR}
		 and u.FWTGZ_YYXTBH = #{fwtgzYyxtbh,jdbcType=VARCHAR}
		 and to_char(u.OPT_TIME,'yyyy-mm-dd') = #{optTime,jdbcType=VARCHAR}
		 and u.OPT_TYPE = #{optType,jdbcType=VARCHAR}
  		order by u.OPT_TIME desc,u.fwtgz_yyxtbh,u.fwbs,u.ffbs) k
		where rownum <= #{end}
		)
		where rnum > #{start}

select count(1) 　 from fwzyffzcb_his u left join FWZYZCB r on u.FWBS=r.FWBS left join JZLBDMB h on u.JZFL=h.JZLBDM    where 1=1

			 and u.FWBS = #{fwbs,jdbcType=VARCHAR}
		 and u.FFBS = #{ffbs,jdbcType=VARCHAR}
